mapper.select:
SELECT c.*, u.nickname, u.username, u.avatar_url as avatarUrl
FROM comment c
LEFT JOIN user u ON c.user_id = u.user_id
WHERE c.post_id = #{postId}
ORDER BY c.created_at ASC
LIMIT #{offset}, #{limit}

SELECT c.*, u.nickname, u.username, u.avatar_url as avatarUrl
FROM comment c
LEFT JOIN user u ON c.user_id = u.user_id
WHERE c.user_id = #{userId}
ORDER BY c.created_at DESC
LIMIT #{offset}, #{limit}

SELECT c.*, u.nickname, u.username, u.avatar_url as avatarUrl
FROM comment c
LEFT JOIN user u ON c.user_id = u.user_id
WHERE c.parent_id = #{parentId}
ORDER BY c.created_at ASC
LIMIT #{offset}, #{limit}

mapper.update:
UPDATE comment
SET content = #{content}
WHERE comment_id = #{commentId}
