mapper.select:
SELECT * FROM favorite 
WHERE user_id = #{userId} AND post_id = #{postId}

    AND folder_id = #{folderId}

LIMIT 1

SELECT * FROM favorite 
WHERE user_id = #{userId}

    AND folder_id = #{folderId}

ORDER BY created_at DESC
LIMIT #{offset}, #{limit}

SELECT * FROM favorite 
WHERE folder_id = #{folderId}
ORDER BY created_at DESC
LIMIT #{offset}, #{limit}

mapper.delete:
DELETE FROM favorite 
WHERE user_id = #{userId} AND post_id = #{postId}

    AND folder_id = #{folderId}
