mapper.select:
SELECT match_id, user1_id, user2_id, created_at, status
FROM match_user
WHERE match_id = #{matchId}

SELECT match_id, user1_id, user2_id, created_at, status
FROM match_user
WHERE user1_id = #{userId} OR user2_id = #{userId}
ORDER BY created_at DESC
LIMIT #{offset}, #{limit}

mapper.update:
UPDATE match_user SET
status = #{status},
WHERE match_id = #{matchId}

mapper.select:
SELECT COUNT(*)
FROM match_user
WHERE user1_id = #{userId} OR user2_id = #{userId}
