mapper.select:
SELECT p.*, u.nickname, u.username, u.avatar_url as avatarUrl
FROM post p
LEFT JOIN user u ON p.user_id = u.user_id
WHERE p.user_id = #{userId} 
ORDER BY p.created_at DESC 
LIMIT #{offset}, #{limit}

mapper.update:
UPDATE post SET
    content = #{content},
    topic = #{topic}
WHERE post_id = #{postId}
